package com.keke.framework.config;import static org.springframework.context.annotation.ComponentScan.Filter;import org.springframework.beans.factory.config.PropertiesFactoryBean;import org.springframework.beans.factory.config.PropertyPlaceholderConfigurer;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.ImportResource;import org.springframework.core.io.ClassPathResource;import org.springframework.stereotype.Controller;import com.keke.Application;@Configuration@ImportResource(value = "classpath:activiti-standalone-context.xml")@ComponentScan(basePackageClasses = Application.class, excludeFilters = @Filter({Controller.class, Configuration.class}))public class ApplicationConfig {		@Bean	public static PropertyPlaceholderConfigurer propertyPlaceholderConfigurer() {		PropertyPlaceholderConfigurer ppc = new PropertyPlaceholderConfigurer();		ppc.setLocations(new ClassPathResource("/persistence.properties"));		return ppc;	}		@Bean	public PropertiesFactoryBean configurePropertiesFactoryBean(){		PropertiesFactoryBean propertiesFactoryBean = new PropertiesFactoryBean();		propertiesFactoryBean.setLocations(new ClassPathResource("/application.properties"));		return propertiesFactoryBean;	}	}